    public function history($type,$id)
    {
        $history = App\Models\History::where('type',$type)->where('type_id',$id)->get();
        $row = "";

        foreach ($history as $key => $value) {
            $row .= "<tr><td>".$value->created_at."</td><td>".$value->msg."</td><td>".getUserInfo($value->action_by,'name')."</td></tr>";
        }

        $pdf = App::make('dompdf.wrapper');
        $pdf->loadHTML('<!DOCTYPE html>
        <html>
        <head>
          <title>PROJECT HISTORY</title>
          <meta http-equiv="Content-Type" content="text/html; charset=utf-8"/>
        </head>
        <style type="text/css">
                @page {
                  margin: 15;
                }
            body
            {
                font-family:DejaVu Sans;
            }
            th,td
            {
                border:1px solid #555;
                font-size:10px;
                page-break-inside: always;

            }
            .tr-space,.td-space
            {
                border:1px solid #FFF;
                font-size:10px;
            }
            .page-break {
                page-break-after: always;
               }
            table
            {
                page-break-inside: always;

            }
        </style>
        <body>
            <center><h2>HISTORY</h2></center>
            <table border="1" width="100%" cellpadding="5" cellspacing="0">
                <thead>
                    <th>DATE</th>
                    <th>ACTION</th>
                    <th>USER</th>
                </thead>
                <tbody>
                    '.$row.'
                </tbody>
            </table>
        </body>
        </html>')
        ->setPaper('A4', 'portrait');
        return $pdf->stream();
    }
